CREATE OR REPLACE FUNCTION check_copia_prescr (IeOpcao_p text, ieAlterarDose_p text, ieEditarSolucao_p text, ieSubstituido_p text, ieTipoItem_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ieTipo_w  	        varchar(1);


BEGIN
   if (IeOpcao_p = 'M') then
      ds_retorno_w     := 'C';
   elsif (IeOpcao_p = 'S') then
      ds_retorno_w     := 'S';
   elsif (IeOpcao_p = 'R') then
		begin
			select 	max(coalesce(ie_marcado,'N') )
			into STRICT    ieTipo_w
			from	regra_item_selecao_prot
			where	coalesce(cd_perfil,obter_perfil_ativo)	 = obter_perfil_ativo
			and	ie_tipo_item			 = ieTipoItem_p
			order by 	1;

			if (ieTipo_w = 'S') then
				ds_retorno_w     := 'C';
			else
				ds_retorno_w     := 'S';
			end if;
		end;
    elsif (IeOpcao_p = 'E') then
		begin
			if (ieSubstituido_p = 'S') then
				ds_retorno_w     := 'S';
			else
				ds_retorno_w     := 'C';
			end if;
		end;
	end if;

    if (ieEditarSolucao_p = 'S') then
        ds_retorno_w     := 'S';
    end if;

	if (IeOpcao_p = 'T') then
        ds_retorno_w     := 'S';
    end if;

    if (ieAlterarDose_p = 'S') then
        ds_retorno_w     := 'S';
    end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
