CREATE OR REPLACE FUNCTION check_dis_prescr ( NR_ATENDIMENTO_P bigint , NR_SEQUENCIA_P bigint ) RETURNS varchar AS $body$
DECLARE

DIS_PRESCR_W varchar(100);

BEGIN

select c.IE_PRESCRICAO_ALTA Into STRICT DIS_PRESCR_W
from cpoe_material a , prescr_material b, prescr_medica c
where a.NR_SEQUENCIA=b.NR_SEQ_MAT_CPOE
and b.NR_PRESCRICAO = c.NR_PRESCRICAO 
and  a.cd_material=b.cd_material
and a.nr_atendimento=NR_ATENDIMENTO_P
and a.nr_sequencia=NR_SEQUENCIA_P  LIMIT 1;

  RETURN DIS_PRESCR_W;
END;
