SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION check_encounter_inconsistancy (NR_ATENDIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE


ds_amount_w	varchar(200) := '';


BEGIN

if (NR_ATENDIMENTO_P IS NOT NULL AND NR_ATENDIMENTO_P::text <> '')	then

	select	CASE WHEN count(*)=0 THEN  obter_desc_expressao(954798)  ELSE '' END 
	into STRICT    ds_amount_w
	from    atendimento_paciente a,
	enc_x_classif_x_category b,
	atend_categoria_convenio c
	where   a.nr_atendimento = NR_ATENDIMENTO_P
	and     a.ie_tipo_atendimento = b.ie_encounter_type
	and     a.nr_seq_classificacao = b.ie_classification
	and     c.nr_atendimento = NR_ATENDIMENTO_P
	and     c.nr_seq_patient_category = b.ie_category;

end if;


return	ds_amount_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
