CREATE OR REPLACE FUNCTION check_exist_hours_gas_therapy ( nr_prescricao_p bigint, nr_seq_gasoterapia_p bigint ) RETURNS varchar AS $body$
DECLARE

ie_exist_hour_w	varchar(1) := 'N';


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_seq_gasoterapia_p IS NOT NULL AND nr_seq_gasoterapia_p::text <> '') then
	begin
	
		select	coalesce(max('S'), 'N') ie_exist_hour
		into STRICT ie_exist_hour_w
		from	prescr_gasoterapia_hor a
		where	a.nr_prescricao	   				= nr_prescricao_p
		and		a.nr_seq_gasoterapia			= nr_seq_gasoterapia_p
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		coalesce(a.dt_fim_horario::text, '') = ''
		and		(a.dt_lib_horario IS NOT NULL AND a.dt_lib_horario::text <> '')
		and		coalesce(a.ie_horario_especial,'N') 	= 'N';
			
	end;
	
end if;

return ie_exist_hour_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
