CREATE OR REPLACE FUNCTION check_pat_cp_measure_is_open (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

nr_count_w      bigint := 0;
ds_retorno_w	varchar(1) := 'N';


BEGIN
	select  count(*)
			into STRICT nr_count_w
	from    patient_cp_indic_measure pcpim,
			patient_cp_indicator pcpi,
			patient_cp_goal pcpg
	where   coalesce(pcpim.dt_liberacao::text, '') = ''
    and     pcpi.nr_sequencia = pcpim.nr_seq_pat_cp_ind
	and     pcpg.nr_sequencia = pcpi.nr_seq_pat_cp_goal
	and     pcpg.nr_seq_prescr_diag = nr_sequencia_p;

    if (nr_count_w > 0) then
        ds_retorno_w := 'S';
    end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
