CREATE OR REPLACE FUNCTION check_prescr_class_ote_is_null (nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE

    nr_count_w          bigint := 0;
    ds_retorno_w	    varchar(1) := 'N';

BEGIN
    select  count(*)
    into STRICT    nr_count_w
    from    pe_prescr_proc
    where   nr_seq_prescr = nr_seq_prescr_p
    and     coalesce(ie_classif_ote::text, '') = '';

    if (nr_count_w > 0) then
        ds_retorno_w := 'Y';
    end if;

	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
