SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION check_rule_insurance_prio_one ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


ds_return_w	varchar(1);
qt_insurance_w  integer;
is_selfpayer_w	varchar(1);

  item RECORD;

BEGIN

ds_return_w := 'N';

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select  count(1)
	into STRICT	qt_insurance_w
	from    atend_categoria_convenio acc
	where   acc.nr_atendimento = nr_atendimento_p;

	for item in (	select  acc.nr_prioridade,
				acc.cd_convenio
			from    atend_categoria_convenio acc
			where   acc.nr_atendimento = nr_atendimento_p) loop

		if (ds_return_w = 'N') then

			if (qt_insurance_w = 1 and item.nr_prioridade <> 1) then

				select  coalesce(max('S'), 'N')
				into STRICT	is_selfpayer_w
				from    convenio c
				where   c.ie_tipo_convenio = 1
				and     c.cd_convenio = item.cd_convenio;

				if (is_selfpayer_w = 'S') then
					ds_return_w := 'S';
				else
					ds_return_w := 'N';
				end if;

			elsif (qt_insurance_w > 1 and item.nr_prioridade <> 1) then
				ds_return_w := 'N';
			else	
				ds_return_w := 'S';
			end if;

		end if;
	end loop;
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
