CREATE OR REPLACE FUNCTION ciha_obter_desc_coluna (nm_coluna_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN
	select 	substr(obter_desc_expressao(cd_exp_label_grid,ds_label_grid),1,255) ds
	into STRICT	ds_retorno_w
	from 	tabela_visao_atributo
	where 	nr_sequencia = 82751
	and	  	nm_atributo = nm_coluna_p;

return	ds_retorno_w;

end;
