CREATE OR REPLACE FUNCTION ciha_obter_se_proc_internado ( cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(1);
qt_ambulatorial_w	bigint;


BEGIN

begin
	select	nr_sequencia
	into STRICT	qt_ambulatorial_w
	from	sus_procedimento_modal
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	cd_modalidade = 1;
exception
	when 	no_data_found then
		qt_ambulatorial_w := 0;
end;

if (qt_ambulatorial_w = 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	sus_procedimento_modal
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	cd_modalidade = 2;
end if;

return	ds_retorno_w;

end;
