CREATE OR REPLACE FUNCTION ciha_obter_se_proc_tipo_reg ( cd_procedimento_p bigint, ie_origem_proced_p bigint, tipo_reg_p bigint) RETURNS varchar AS $body$
DECLARE


/* Segundo orientações técnicas do CIHA - Portaria 1.174

" Os procedimentos de Instrumento de Registro 01- BPAC, 02-BPAI e 06-APAC principal são
considerados de Modalidade Ambulatorial "

" Os procedimentos de Instrumento de Registro 03-AIH principal são considerados de
Modalidade Internação "
 */
/* tipo_reg_p
 - 2 - Internado
 - 4 - Ambulatorial
 - 5 - Ambulatorial Consolidado */
ds_retorno_w	varchar(1);
cd_registro_w	smallint;


BEGIN

ds_retorno_w := 'N';
case
when	tipo_reg_p = 2 then -- Internado
	begin
		cd_registro_w := Sus_Obter_TipoReg_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 2); -- AIH
	exception
		when others then cd_registro_w := 0;
	end;

	If (cd_registro_w > 0) then
		ds_retorno_w := 'S';
	Else	ds_retorno_w := 'N';
	End if;

when	tipo_reg_p in (4, 5) then -- Ambulatorial (Individualizado ou consolidado
	begin
		cd_registro_w := Sus_Obter_TipoReg_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 1) + -- BPA
				 Sus_Obter_TipoReg_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 3);  -- APAC
	exception
		when others then cd_registro_w := 0;
	end;

	If (cd_registro_w > 0) then
		ds_retorno_w := 'S';
	Else	ds_retorno_w := 'N';
	End if;
else	ds_retorno_w := 'N';
end case;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
