CREATE OR REPLACE FUNCTION cih_contato_realizado (nr_ficha_ocorrencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_contato_realizado_w 		varchar(1);
qt_contato_w 			bigint;
nr_atendimento_w	bigint;


BEGIN

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	cih_ficha_ocorrencia
	where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;

	select	count(*)
	into STRICT 	qt_contato_w
	from	cih_contato
	where	((nr_ficha_ocorrencia = nr_ficha_ocorrencia_p)
	or ((nr_atendimento = nr_atendimento_w) and (coalesce(nr_ficha_ocorrencia::text, '') = '')));

	if (qt_contato_w = 0) then
		ie_contato_realizado_w := 'N';
	else
		ie_contato_realizado_w := 'S';
	end if;


return	ie_contato_realizado_w;

end;
