CREATE OR REPLACE FUNCTION cih_desc_classif_hmaos (nr_seq_classificacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	cih_classif_hig_maos.ds_classificacao%type;

BEGIN

if (nr_seq_classificacao_p IS NOT NULL AND nr_seq_classificacao_p::text <> '') then

	select	max(ds_classificacao) ds_classificacao
	into STRICT	ds_classificacao_w
	from (SELECT ds_classificacao
		from	cih_classif_hig_maos
		where	nr_sequencia = nr_seq_classificacao_p
		
union

		SELECT	substr(obter_desc_expressao(657982, null), 1, 255) ds_classificacao
		
		where	9999 = nr_seq_classificacao_p) alias4;

end if;

return	ds_classificacao_w;

end;
$body$
