CREATE OR REPLACE FUNCTION cih_desc_momento_hig_maos (ie_momento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);

BEGIN

if (ie_momento_p IS NOT NULL AND ie_momento_p::text <> '') then

	select	substr(max(ds_momento), 1, 255) ds_momento
	into STRICT	ds_retorno_w
	from (SELECT	substr(nr_seq_apresentacao || ' - ' || ds_momento, 1, 255) ds_momento
		from	cih_momento_hig_maos
		where	nr_sequencia = ie_momento_p
		
union

		SELECT	substr(obter_valor_dominio(7445, ie_momento_p), 1, 255) ds_momento
		) alias6;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
