SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cih_difinicao_expirou ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
qt_horas_w	varchar(10);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	qt_horas_w := Obter_param_Usuario(936, 75, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, qt_horas_w);

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from 	prescr_medica a,
		cih_def_infect b
	where 	a.nr_prescricao = nr_prescricao_p
	and 	a.nr_prescricao = b.nr_prescricao
	and 	a.dt_prescricao < (clock_timestamp() - qt_horas_w / 24)
	and 	not exists ( 	SELECT  1
				from 	cih_def_infect_resp c
				where 	c.nr_seq_def_infect = b.nr_sequencia);

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
