CREATE OR REPLACE FUNCTION cih_obter_dados_contato_ficha (nr_ficha_ocorrencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_houve_contato_w	varchar(20);
nr_seq_motivo_w		bigint;
ie_houve_complicacao_w	varchar(1);
nr_atendimento_w	bigint;


BEGIN

select	max(CIH_OBTER_MOTIVO_CONTATO_ATUAL(nr_ficha_ocorrencia_p))
into STRICT	nr_seq_motivo_w
;

if (coalesce(nr_seq_motivo_w::text, '') = '') then

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	cih_ficha_ocorrencia
	where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;

	select	max(ie_houve_contato)
	into STRICT	ie_houve_contato_w
	from	cih_contato
	where	((nr_ficha_ocorrencia = nr_ficha_ocorrencia_p)
		or ((nr_atendimento = nr_atendimento_w) and (coalesce(nr_ficha_ocorrencia::text, '') = '')))
	and	ie_houve_contato	= 'S';

end if;

if (ie_opcao_p	= 'C') then
	return coalesce(ie_houve_contato_w, 'N');
end if;

if (ie_opcao_p	= 'E') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_houve_complicacao_w
	from	cih_contato
	where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p
	and	ie_complicacao_infect = 'S';

	return coalesce(ie_houve_complicacao_w, 'N');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
