CREATE OR REPLACE FUNCTION cih_obter_dados_ficha ( nr_ficha_ocorrencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
resultado_w		varchar(200);
nr_atendimento_w	bigint;

/*Opcao: 
	1 - Atendimento 
	2 - Paciente 
*/
 
 

BEGIN 
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	cih_ficha_ocorrencia 
where	nr_ficha_ocorrencia	= nr_ficha_ocorrencia_p;
 
if (ie_opcao_p	= 1) then 
	resultado_w	:= nr_atendimento_w;
elsif (ie_opcao_p	= 2) then 
	resultado_w	:= obter_pessoa_atendimento(nr_atendimento_w,'C');
end if;
 
return resultado_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
