DECLARE


ds_retorno_w			varchar(255);
ds_analise_w			varchar(200);


BEGIN

ds_retorno_w := '';

if (nr_seq_precaucao_p IS NOT NULL AND nr_seq_precaucao_p::text <> '') then

	select 	ie_analise
	into STRICT	ds_analise_w
	from 	atendimento_precaucao
	where	nr_sequencia = nr_seq_precaucao_p;

	if (ds_analise_w = 'S') then
		ds_retorno_w := Wheb_mensagem_pck.get_texto(306136); -- Confirmado
	elsif (ds_analise_w = 'N') then
		ds_retorno_w := Wheb_mensagem_pck.get_texto(306137); -- NÃ£o confirmado
	end if;

end if;

return	ds_retorno_w;

end;
