CREATE OR REPLACE FUNCTION cih_obter_desc_sitio_espe_prin (nr_seq_sitio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_sitio_princ_w	bigint;
ds_retorno_w		varchar(4000);
ds_sitio_principal_w	varchar(255);
ds_sitio_especifico_w	varchar(255);

C01 CURSOR FOR
	SELECT	b.ds_sitio_especifico
	from 	niss_sitio_princ_esp a,
		niss_sitio_especifico b
	where 	a.nr_seq_espec 		= b.nr_sequencia
	and 	coalesce(ie_situacao,'A') 	= 'A'
	and	a.nr_seq_princ		= nr_seq_sitio_princ_w
	order by 1;



BEGIN
if (nr_seq_sitio_p IS NOT NULL AND nr_seq_sitio_p::text <> '') then

	select	x.nr_sequencia,
		x.ds_sitio_principal
	into STRICT	nr_seq_sitio_princ_w,
		ds_sitio_principal_w
	from	niss_sitio_principal x
	where	coalesce(x.ie_situacao,'A') 	= 'A'
	and	x.nr_sequencia 		= nr_seq_sitio_p;


	if (ie_opcao_p = 'P') then
		ds_retorno_w	:= ds_sitio_principal_w;
	elsif (ie_opcao_p = 'E') then
		open c01;
		loop
		fetch c01 into
			ds_sitio_especifico_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_retorno_w	:= ds_retorno_w||'   -   '||ds_sitio_especifico_w;
		end;
	end loop;
	close c01;

	if (length(ds_retorno_w) > 0) then
		ds_Retorno_w	:= substr(ds_retorno_w,7,length(ds_retorno_w));
	end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
