CREATE OR REPLACE FUNCTION cih_obter_dias_internacao_uti ( dt_entrada_p timestamp, dt_alta_p timestamp, qt_dias_uti_p bigint) RETURNS bigint AS $body$
DECLARE

qt_dias_uti_w		bigint;
qt_dias_internado_w	bigint;

/* OS78858 Sírio, quando a quantidade de dias de UTI for igual a quantidade de dias de internação diminuir um dia */

BEGIN

if (dt_entrada_p IS NOT NULL AND dt_entrada_p::text <> '') and (dt_alta_p IS NOT NULL AND dt_alta_p::text <> '') and (qt_dias_uti_p IS NOT NULL AND qt_dias_uti_p::text <> '') then

	qt_dias_internado_w	:= ((trunc(dt_alta_p) - trunc(dt_entrada_p))+1);

	if (qt_dias_internado_w	= qt_dias_uti_p) then
		qt_dias_internado_w	:= qt_dias_internado_w - 1;
	else
		qt_dias_internado_w	:= qt_dias_uti_p;
	end if;

	qt_dias_uti_w		:= qt_dias_internado_w;

end if;

return coalesce(qt_dias_uti_w, 0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
