CREATE OR REPLACE FUNCTION cih_obter_dias_uteis ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_material_p prescr_material.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


qt_dias_uteis_w			prescr_material.nr_dia_util%type;
nr_seq_mat_cpoe_w		prescr_material.nr_seq_mat_cpoe%type;
					

BEGIN
qt_dias_uteis_w	:= 0;

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	select	max(a.nr_seq_mat_cpoe),
			coalesce(max(nr_dia_util),0)
	into STRICT	nr_seq_mat_cpoe_w,
			qt_dias_uteis_w
	from	prescr_material	a
	where	a.nr_sequencia	= nr_seq_material_p
	and		a.nr_prescricao	= nr_prescricao_p;
	
	
	if (obter_se_prescricao_cpoe(nr_prescricao_p, '') = 'S') then
		qt_dias_uteis_w	:= cih_obter_dias_util_cpoe(nr_seq_mat_cpoe_w);
	end if;
end if;

return	qt_dias_uteis_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
