CREATE OR REPLACE FUNCTION cih_obter_dias_util_cpoe (nr_seq_mat_cpoe_p bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	cpoe_material.nr_atendimento%type;
cd_pessoa_fisica_w	cpoe_material.cd_pessoa_fisica%type;
cd_material_w	cpoe_material.cd_material%type;
dt_inicio_w	cpoe_material.dt_inicio%type;
dt_fim_w	cpoe_material.dt_fim%type;
dt_fim_cih_w	cpoe_material.dt_fim_cih%type;
dt_suspensao_w	cpoe_material.dt_suspensao%type;
dt_lib_suspensao_w	cpoe_material.dt_lib_suspensao%type;
nr_dia_util_w	cpoe_material.nr_dia_util%type;
qt_total_dias_lib_w	cpoe_material.qt_dias_liberado%type;
ie_tratamento_vig_w	varchar(5);
	

BEGIN
if (nr_seq_mat_cpoe_p IS NOT NULL AND nr_seq_mat_cpoe_p::text <> '') then

	select	max(nr_atendimento),
		max(cd_pessoa_fisica),
		max(cd_material),
		max(dt_inicio),
		max(dt_fim),
		max(dt_fim_cih),
		max(dt_suspensao),
		max(dt_lib_suspensao)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w,
		cd_material_w,
		dt_inicio_w,
		dt_fim_w,
		dt_fim_cih_w,
		dt_suspensao_w,
		dt_lib_suspensao_w
	from	cpoe_material
	where	nr_sequencia = nr_seq_mat_cpoe_p;

	SELECT * FROM cpoe_obter_nr_dias_util_cih(nr_seq_mat_cpoe_p, nr_atendimento_w, cd_pessoa_fisica_w, cd_material_w, dt_inicio_w, dt_fim_w, dt_fim_cih_w, dt_suspensao_w, dt_lib_suspensao_w, null, nr_dia_util_w, ie_tratamento_vig_w, qt_total_dias_lib_w) INTO STRICT nr_dia_util_w, ie_tratamento_vig_w, qt_total_dias_lib_w;
end if;

return nr_dia_util_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
