CREATE OR REPLACE FUNCTION cih_obter_int_micro_med ( cd_microorganismo_p bigint, cd_medicamento_p bigint, nr_ficha_ocorrencia_p bigint, dt_inicial_p text, dt_final_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN


select	coalesce(sum(CASE WHEN a.cd_microorganismo=cd_microorganismo_p THEN a.nr_intermediario  ELSE 0 END ),0)
into STRICT	ds_retorno_w
from	cih_cultura_medic_v a
where	((trunc(a.dt_alta_interno) between dt_inicial_p and fim_dia(dt_final_p)) or (CASE WHEN dt_inicial_p='  /  /    ' THEN  null  ELSE dt_inicial_p coalesce(END::text, '') = '') or (CASE WHEN dt_final_p='  /  /    ' THEN  null  ELSE dt_final_p coalesce(END::text, '') = ''))
and	a.cd_microorganismo = cd_microorganismo_p
and (a.nr_ficha_ocorrencia = nr_ficha_ocorrencia_p or nr_ficha_ocorrencia_p = 0)
and (a.cd_medicamento = cd_medicamento_p);


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
