CREATE OR REPLACE FUNCTION cih_obter_isolamento_obs_setor (cd_doenca_cid_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_doenca_inf_w	bigint;
ds_retorno_w		varchar(4000);


BEGIN
select	max(a.nr_sequencia)
into STRICT	nr_seq_doenca_inf_w
from	cih_doenca_infecto a,
	cih_doenca_infecto_setor b
where	a.nr_sequencia = b.nr_seq_doenca
and	a.cd_doenca_cid = cd_doenca_cid_p
and	b.cd_setor_atendimento = cd_setor_atendimento_p;

if (nr_seq_doenca_inf_w IS NOT NULL AND nr_seq_doenca_inf_w::text <> '') then
	select	obter_tipo_isolamento_cid(cd_doenca_cid_p,0,'TO')
	into STRICT	ds_retorno_w
	;
else
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_doenca_inf_w
	from	cih_doenca_infecto a
	where	a.cd_doenca_cid = cd_doenca_cid_p
	and	not exists (SELECT	1
				from	cih_doenca_infecto_setor b
				where	a.nr_sequencia = b.nr_seq_doenca);
	if (nr_seq_doenca_inf_w IS NOT NULL AND nr_seq_doenca_inf_w::text <> '') then
		select	obter_tipo_isolamento_cid(cd_doenca_cid_p,0,'TO')
		into STRICT	ds_retorno_w
		;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
