CREATE OR REPLACE FUNCTION cih_obter_micro_med ( cd_microorganismo_p bigint, cd_medicamento_p bigint, nr_ficha_ocorrencia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_resultado_cultura_p bigint, nr_seq_cultura_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

select	count(*)
into STRICT	ds_retorno_w
from  	cih_ficha_ocorrencia f,
	cih_cultura_medic b,
	cih_cultura a
where	a.dt_coleta between trunc(dt_inicial_p) and fim_dia(dt_final_p)
and	a.cd_microorganismo	= cd_microorganismo_p
and (f.nr_ficha_ocorrencia	= nr_ficha_ocorrencia_p or nr_ficha_ocorrencia_p = 0)
and	b.cd_medicamento	= cd_medicamento_p
and	a.nr_seq_cultura	= nr_seq_cultura_p
and	a.nr_ficha_ocorrencia	= f.nr_ficha_ocorrencia
and  	a.nr_ficha_ocorrencia	= b.nr_ficha_ocorrencia
and   	a.nr_seq_cultura	= b.nr_seq_cultura
and	b.ie_resultado_cultura	= CASE WHEN ie_resultado_cultura_p=0 THEN b.ie_resultado_cultura  ELSE ie_resultado_cultura_p END;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
