CREATE OR REPLACE FUNCTION cih_obter_motivo_contato_atual (nr_ficha_ocorrencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_motivo_w		bigint;
nr_atendimento_w	bigint;
dt_contato_w		timestamp;


BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	cih_ficha_ocorrencia
where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;

select	max(dt_contato)
into STRICT	dt_contato_w
from	cih_contato
where	((nr_ficha_ocorrencia = nr_ficha_ocorrencia_p)
	or	((nr_atendimento = nr_atendimento_w) and (coalesce(nr_ficha_ocorrencia::text, '') = '')));

select	max(nr_seq_motivo)
into STRICT	nr_seq_motivo_w
from	cih_contato
where	((nr_ficha_ocorrencia = nr_ficha_ocorrencia_p)
		or ((nr_atendimento = nr_atendimento_w) and (coalesce(nr_ficha_ocorrencia::text, '') = '')))
and	dt_contato = dt_contato_w;

return nr_seq_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
