CREATE OR REPLACE FUNCTION cih_obter_nr_acesso_dicom (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (nr_seq_laudo_p IS NOT NULL AND nr_seq_laudo_p::text <> '') then

	SELECT 	MAX(a.nr_acesso_dicom)
	into STRICT	ds_retorno_w
	from   	prescr_procedimento a,
		laudo_paciente b
	where  	a.nr_prescricao = b.nr_prescricao
	and	a.nr_sequencia = b. nr_seq_prescricao
	and	b.nr_sequencia = nr_seq_laudo_p;

end if;

return	ds_retorno_w;

end;
