CREATE OR REPLACE FUNCTION cih_obter_setor_infeccao_niss ( nr_ficha_ocorrencia_p bigint, dt_infeccao_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_setor_atual_w	varchar(100);
cd_setor_atendimento_w	integer;
nr_atendimento_w	bigint;
dt_entrada_unidade_w	timestamp;
dt_saida_unidade_w	timestamp;
cd_setor_origem_w	integer;

c00 CURSOR FOR
SELECT	cd_setor_atendimento
from	atend_paciente_unidade
where	nr_atendimento		= nr_atendimento_w
and	coalesce(dt_saida_unidade,pkg_date_utils.get_dateTime(2150, 12, 31)) 	>= dt_infeccao_p - 2
and	dt_entrada_unidade 	<= dt_infeccao_p - 2
order by dt_entrada_unidade;

c01 CURSOR FOR
SELECT	cd_setor_atendimento
from	atend_paciente_unidade
where	nr_atendimento		= nr_atendimento_w
and	coalesce(dt_saida_unidade,pkg_date_utils.get_dateTime(2150, 12, 31)) 	>= dt_infeccao_p
and	dt_entrada_unidade 	<= dt_infeccao_p
order by dt_entrada_unidade;


BEGIN

select	max(a.nr_atendimento),
	max(cd_setor_origem)
into STRICT	nr_atendimento_w,
	cd_setor_origem_w
from	cih_local_infeccao b,
	cih_ficha_ocorrencia a
where	a.nr_ficha_ocorrencia = b.nr_ficha_ocorrencia
and	a.nr_ficha_ocorrencia	= nr_ficha_ocorrencia_p
and	b.dt_infeccao = dt_infeccao_p;

open C00;
loop
fetch C00 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin
	cd_setor_atendimento_w	:= cd_setor_atendimento_w;
	end;
end loop;
close C00;

if (coalesce(cd_setor_atendimento_w::text, '') = '') then
	open c01;
	loop
	fetch c01 into
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		cd_setor_atendimento_w	:= cd_setor_atendimento_w;
	end loop;
	close c01;
end if;

cd_setor_atendimento_w := coalesce(cd_setor_origem_w,cd_setor_atendimento_w);

if (ie_opcao_p = 'D') then
	ds_setor_atual_w	:= substr(obter_nome_setor(cd_setor_atendimento_w),1,100);
elsif (ie_opcao_p = 'C') then
	ds_setor_atual_w	:= cd_setor_atendimento_w;
end if;

return	ds_setor_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
