CREATE OR REPLACE FUNCTION cih_obter_setor_origem (nr_ficha_ocorrencia_p bigint, dt_infeccao_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
'D' - Descricao do setor
'C' - CÃ³digo do setor
*/
ds_setor_atual_w	varchar(100);
cd_setor_atendimento_w	integer;
nr_atendimento_w	bigint;
dt_entrada_unidade_w	timestamp;
dt_saida_unidade_w	timestamp;

c01 CURSOR FOR
SELECT	cd_setor_atendimento
from	atend_paciente_unidade
where	nr_atendimento		= nr_atendimento_w
and	coalesce(dt_saida_unidade,to_date('31/12/2150','dd/mm/yyyy')) 	>= dt_infeccao_p
and	dt_entrada_unidade 	<= dt_infeccao_p
order by dt_entrada_unidade;


BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	cih_ficha_ocorrencia
where	nr_ficha_ocorrencia	= nr_ficha_ocorrencia_p;

open c01;
loop
fetch c01 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (ie_opcao_p	= 'D') then
		ds_setor_atual_w	:= substr(obter_nome_setor(cd_setor_atendimento_w),1,100);
	else
		ds_setor_atual_w	:= cd_setor_atendimento_w;
	end if;

end loop;
close c01;

return	ds_setor_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
