CREATE OR REPLACE FUNCTION cih_obter_se_cid_compativel ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_cid_doenca_p text) RETURNS varchar AS $body$
DECLARE


qt_proc_cid_w	integer;
ie_compativel_w	varchar(1);


BEGIN

ie_compativel_w	:= 'N';

select	count(*)
into STRICT	qt_proc_cid_w
from	sus_procedimento_cid
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	cd_doenca_cid		= cd_cid_doenca_p;

if (qt_proc_cid_w > 0) then
	ie_compativel_w := 'S';
end if;

return ie_compativel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
