CREATE OR REPLACE FUNCTION cih_obter_se_cid_comp_opcao ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_cid_doenca_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*IE_OPCAO_P
N - Não consiste
S - Consiste conforme SUS unificado
U - Consistir somente se Nulo
I - Consiste conforme SUS unificado e não consiste quando não possuir nenhum CID compativel
*/
qt_proc_cid_w	integer;
ie_compativel_w	varchar(1);
qt_cid_compat_w	integer;


BEGIN

ie_compativel_w	:= 'N';

if (ie_opcao_p = 'S') then

	select	count(*)
	into STRICT	qt_proc_cid_w
	from	sus_procedimento_cid
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	cd_doenca_cid		= cd_cid_doenca_p;

	if (qt_proc_cid_w > 0) then
		ie_compativel_w := 'S';
	end if;

elsif (ie_opcao_p = 'U') then

	if (cd_cid_doenca_p IS NOT NULL AND cd_cid_doenca_p::text <> '') then
		ie_compativel_w	:= 'S';
	end if;
elsif (ie_opcao_p = 'I') then
	select	count(*)
	into STRICT	qt_cid_compat_w
	from	sus_procedimento_cid
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;
	if (qt_cid_compat_w = 0) then
		ie_compativel_w	:= 'S';
	elsif (qt_cid_compat_w > 0) then
		select	count(*)
		into STRICT	qt_proc_cid_w
		from	sus_procedimento_cid
		where	cd_procedimento		= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p
		and	cd_doenca_cid		= cd_cid_doenca_p;
		if (qt_proc_cid_w > 0) then
			ie_compativel_w := 'S';
		end if;
	end if;
else
	ie_compativel_w	:= 'S';
end if;


return ie_compativel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
