CREATE OR REPLACE FUNCTION cih_obter_se_exame_dep_antib (nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_microorganismo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_grupo_mic_w		bigint;
nr_seq_exame_w		bigint;


BEGIN

select	cih_obter_grupo_mic(cd_microorganismo_p)
into STRICT	nr_grupo_mic_w
;

select	max(nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia = nr_seq_prescr_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	exame_lab_dependente y,
	prescr_procedimento w,
	exame_laboratorio q,
	exame_lab_result_antib x,
	exame_lab_result_item k,
	exame_lab_resultado t
where	y.ie_geracao = 3
and	y.nr_seq_exame = nr_seq_exame_w
and	w.nr_prescricao = nr_prescricao_p
and	w.nr_seq_exame = y.nr_seq_exame_dep
and	w.nr_seq_superior = nr_seq_prescr_p
and	q.nr_seq_exame = y.nr_seq_exame_dep
and	k.nr_seq_prescr = w.nr_sequencia
and	w.nr_prescricao = t.nr_prescricao
and	t.nr_seq_resultado = k.nr_seq_resultado
and	k.nr_seq_resultado = x.nr_seq_resultado
and	k.nr_sequencia = x.nr_seq_result_item
and	x.ie_resultado not in ('R','S','I')
and	q.ie_formato_resultado = 'SM'
and	coalesce(y.ie_gera_antibiograma,'N') = 'S'
and	coalesce(y.nr_seq_grupo_micro,0) = coalesce(nr_grupo_mic_w,0);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
