CREATE OR REPLACE FUNCTION cih_obter_se_possui_avaliacao (cd_pessoa_fisica_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w varchar(1);
cd_avaliacao_w bigint;


BEGIN 
cd_avaliacao_w := Obter_param_Usuario(7043, 25, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, cd_avaliacao_w);
 
select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_retorno_w 
from	med_avaliacao_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
--and	nr_atendimento	 = nr_atendimento_p 
and	nr_seq_tipo_avaliacao = cd_avaliacao_w 
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
and	ie_situacao = 'A';
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
