CREATE OR REPLACE FUNCTION cih_obter_se_possui_opme (nr_seq_cirurgia_pac_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	agenda_pac_opme a,
	cirurgia	b,
	cih_cirurgia 	c,
	agenda_paciente d
where	a.nr_seq_agenda = d.nr_sequencia
and	b.nr_cirurgia	= d.nr_cirurgia
and	b.nr_cirurgia	= c.nr_seq_cirurgia_pac
and	c.nr_seq_cirurgia_pac = nr_seq_cirurgia_pac_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
