CREATE OR REPLACE FUNCTION cih_obter_tipo_infeccao (cd_caso_infeccao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

select	coalesce(max(ie_tipo_Caso),cd_caso_infeccao_p)
into STRICT	ds_retorno_w
from	cih_Caso_infeccao
where	cd_Caso_infeccao	= cd_Caso_infeccao_p
and	ie_situacao		= 'A';

return	ds_retorno_w;

end;
$body$
