CREATE OR REPLACE FUNCTION cih_obter_tipo_util (cd_medicamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		smallint;
ie_objetivo_w	varchar(1);


BEGIN

select	max(ie_objetivo)
into STRICT	ie_objetivo_w
from	rep_objetivo_medicamento
where	cd_material = cd_medicamento_p;

if (ie_objetivo_w = 'F') then
	ds_retorno_w := 1;
elsif (ie_objetivo_w = 'T') then
	ds_retorno_w := 2;
elsif (ie_objetivo_w = 'C') then
	ds_retorno_w := 3;
elsif (ie_objetivo_w = 'D') then
	ds_retorno_w := 4;
elsif (ie_objetivo_w = 'E') then
	ds_retorno_w := 5;
elsif (ie_objetivo_w = 'P') then
	ds_retorno_w := 6;
elsif (ie_objetivo_w = 'N') then
	ds_retorno_w := 7;

end if;

return	ds_retorno_w;

end;
$body$
