CREATE OR REPLACE FUNCTION cih_obt_se_existe_resp_infect (nr_atendimento_p bigint, nr_horas_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_defin_w	varchar(1) := 'N';

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_defin_w
	FROM material c, prescr_medica b, prescr_material a
LEFT OUTER JOIN prescr_material_cid d ON (a.nr_prescricao = d.nr_prescricao AND a.nr_sequencia = d.nr_sequencia_medic)
WHERE a.nr_prescricao		= b.nr_prescricao   and a.cd_material		= c.cd_material and c.ie_controle_medico 	> 0 and coalesce(nr_cirurgia::text, '') = '' and not exists (SELECT	1
				from	cirurgia g
				where	g.nr_prescricao = b.nr_prescricao) and exists (select	1
				from	cih_def_infect t
				where	t.nr_atendimento	= nr_atendimento_p
				and	t.nr_prescricao		= b.nr_prescricao
				and	exists (select	1
						 from	cih_def_infect_resp r
						 where	r.nr_seq_def_infect = t.nr_sequencia)) and b.dt_prescricao		> clock_timestamp() - ( nr_horas_prescr_p / 24 ) and b.nr_atendimento	= nr_atendimento_p;

end if;

return ie_existe_defin_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
