CREATE OR REPLACE FUNCTION cih_possui_infect_contato ( nr_ficha_ocorrencia_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

BEGIN
ds_retorno_w := 'N';
if (nr_ficha_ocorrencia_p IS NOT NULL AND nr_ficha_ocorrencia_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	cih_contato
	where	((nr_ficha_ocorrencia = nr_ficha_ocorrencia_p)
		or	((nr_atendimento = nr_atendimento_p) and (coalesce(nr_ficha_ocorrencia::text, '') = '')))
	and	ie_complicacao_infect = 'S';
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
