CREATE OR REPLACE FUNCTION cih_qtd_dias_medic_util (nr_seq_medicamento_p bigint, nr_ficha_ocorrencia_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		integer;
qt_dias_util		integer;
dt_inicial_w		timestamp;
dt_final_w		timestamp;


BEGIN
if (nr_seq_medicamento_p IS NOT NULL AND nr_seq_medicamento_p::text <> '') and (nr_ficha_ocorrencia_p IS NOT NULL AND nr_ficha_ocorrencia_p::text <> '') then
	select	trunc(dt_inicial_utilizacao),
		trunc(dt_final_utilizacao)
	into STRICT	dt_inicial_w,
		dt_final_w
	from	cih_medic_utilizado
	where	nr_seq_medicamento = nr_seq_medicamento_p
	and	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;
end if;

if (dt_final_w IS NOT NULL AND dt_final_w::text <> '' AND dt_inicial_w IS NOT NULL AND dt_inicial_w::text <> '') then
	if (dt_final_w >= dt_inicial_w) then
		qt_dias_util := (dt_final_w - dt_inicial_w);
	end if;
elsif (coalesce(dt_final_w::text, '') = '') then
	qt_dias_util := (trunc(clock_timestamp()) - dt_inicial_w);
end if;

ds_retorno_w := qt_dias_util;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
