SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cirurgia_column_bill ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_return_w		varchar(255);
qt_material_w	double precision;
qt_material_total_w	double precision;


BEGIN

if (nr_prescricao_p	> 0) and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	--Verified
	select 	coalesce(sum(a.qt_material),0)
	into STRICT	qt_material_w
	from	prescr_material a
	where	a.nr_prescricao = nr_prescricao_p
	and	coalesce(a.cd_motivo_baixa,0) <> 0;


	select 	coalesce(sum(a.qt_material),0)
	into STRICT	qt_material_total_w
	from	prescr_material a
	where	a.nr_prescricao = nr_prescricao_p;



	if (qt_material_total_w <> 0) then
		ds_return_w	:= qt_material_w ||' / '||qt_material_total_w;
	end if;



end if;


return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
