CREATE OR REPLACE FUNCTION cirurgia_descricao_newrecord ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint , cd_responsavel_p text) RETURNS bigint AS $body$
DECLARE


qt_cirurgia_descricao_w		bigint := 0;


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '')
	and (nr_cirurgia_p > 0) then
	select	count(*)
	into STRICT	qt_cirurgia_descricao_w
	from 	cirurgia_descricao
	where 	nr_cirurgia 	= nr_cirurgia_p
	and 	cd_responsavel	= cd_responsavel_p
	and  	coalesce(ie_situacao,'A') = 'A';
elsif (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then
	select	count(*)
	into STRICT	qt_cirurgia_descricao_w
	from 	cirurgia_descricao
	where 	nr_seq_pepo 	= nr_seq_pepo_p
	and 	cd_responsavel	= cd_responsavel_p
	and  	coalesce(ie_situacao,'A')  = 'A';
end if;

return qt_cirurgia_descricao_w;

end;
$body$
