CREATE OR REPLACE FUNCTION clipboard_info_concat ( gen_w bigint ) RETURNS varchar AS $body$
DECLARE


    ds_retorno_w   varchar(2000);
    nur_team_w     varchar(2000);
    c01 CURSOR FOR
    SELECT
        obter_nome_pf(cd_profissional)
    FROM
        pf_profissional
    WHERE
        cd_paciente = gen_w;


BEGIN
    OPEN c01;
    LOOP
        FETCH c01 INTO nur_team_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */
        ds_retorno_w := nur_team_w
                        || ';'
                        || ds_retorno_w;
    END LOOP;

    CLOSE c01;
    RETURN substr(ds_retorno_w, 0, length(ds_retorno_w) - 1);
END;
$body$
