SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION clipboard_medico_concat ( gen_w bigint ) RETURNS varchar AS $body$
DECLARE


    ds_retorno_w   varchar(2000);
    medico_team_w     varchar(2000);
    c01 CURSOR FOR
   SELECT
                nm_medico


            FROM (
                    SELECT DISTINCT
                        substr(obter_nome_pf(a.cd_prescritor), 1, 100) nm_medico,
                        substr(obter_especialidade_medico(a.cd_prescritor, 'D'), 1, 100) ds_especialidade
                    FROM
                        pessoa_fisica   c,
                        medico          b,
                        prescr_medica   a
                    WHERE
                        b.cd_pessoa_fisica = c.cd_pessoa_fisica
                        AND a.cd_prescritor = b.cd_pessoa_fisica
                        AND a.nr_atendimento = gen_w
                        AND b.ie_situacao = 'A'

UNION

                    SELECT DISTINCT
                        substr(obter_nome_pf(a.cd_medico), 1, 100) nm_medico,
                        substr(obter_especialidade_medico(a.cd_medico, 'D'), 1, 100) ds_especialidade
                    FROM
                        pessoa_fisica       c,
                        medico              b,
                        evolucao_paciente   a
                    WHERE
                        b.cd_pessoa_fisica = c.cd_pessoa_fisica
                        AND a.cd_medico = b.cd_pessoa_fisica
                        AND a.nr_atendimento = gen_w
                        AND b.ie_situacao = 'A'
                    
UNION

                    SELECT DISTINCT
                        substr(obter_nome_pf(a.cd_medico_referido), 1, 100) nm_medico,
                        substr(obter_especialidade_medico(a.cd_medico_referido, 'D'), 1, 100) ds_especialidade
                    FROM
                        pessoa_fisica          c,
                        medico                 b,
                        atendimento_paciente   a
                    WHERE
                        b.cd_pessoa_fisica = c.cd_pessoa_fisica
                        AND a.cd_medico_referido = b.cd_pessoa_fisica
                        AND a.nr_atendimento = gen_w
                        AND b.ie_situacao = 'A'
                ) alias12;


BEGIN
    OPEN c01;
    LOOP
        FETCH c01 INTO medico_team_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */
        ds_retorno_w := medico_team_w
                        || ';'
                        || ds_retorno_w;
    END LOOP;

    CLOSE c01;
    RETURN substr(ds_retorno_w, 0, length(ds_retorno_w) - 1);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
