CREATE OR REPLACE FUNCTION clip_get_type_def ( record_type_p text ) RETURNS bigint AS $body$
DECLARE

    nr_seq_p bigint;

BEGIN
    SELECT
        nr_sequencia
    INTO STRICT nr_seq_p
    FROM
        clinical_note_settings
    WHERE
        ie_data_type = 'CLIPBOARD'
        AND ie_med_rec_type = record_type_p
        AND ie_default_rule = 'S'
        AND ie_situacao = 'A';

    RETURN nr_seq_p;
END;
$body$
