CREATE OR REPLACE FUNCTION cmc_obter_passagens_setor (nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_setor_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



nr_retorno_w	integer;

/*
 C - Número de vezes que o paciente passou pelo setor
 P - NR_SEQUENCIA da primeira passagem pelo setor do parâmetro
 U - NR_SEQUENCIA da ultima passagem pelo setor do parâmetro
*/
BEGIN


if (ie_opcao_p = 'C') then
	select	count(*)
	into STRICT	nr_retorno_w
	from 	atend_paciente_unidade
	where	nr_atendimento		= nr_atendimento_p
	and	cd_setor_atendimento	= cd_setor_p
	and	((dt_entrada_unidade	between dt_inicial_p and dt_final_p)
		or (dt_saida_unidade	between dt_inicial_p and dt_final_p));
end if;

if (ie_opcao_p = 'P') then
	select	min(nr_sequencia)
	into STRICT	nr_retorno_w
	from 	atend_paciente_unidade
	where	nr_atendimento		= nr_atendimento_p
	and	cd_setor_atendimento	= cd_setor_p
	and	((dt_entrada_unidade	between dt_inicial_p and dt_final_p)
		or (dt_saida_unidade	between dt_inicial_p and dt_final_p));
end if;


if (ie_opcao_p = 'U') then
	select	max(nr_sequencia)
	into STRICT	nr_retorno_w
	from 	atend_paciente_unidade
	where	nr_atendimento		= nr_atendimento_p
	and	cd_setor_atendimento	= cd_setor_p
	and	((dt_entrada_unidade	between dt_inicial_p and dt_final_p)
		or (dt_saida_unidade	between dt_inicial_p and dt_final_p));
end if;


return nr_retorno_w;


end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
