CREATE OR REPLACE FUNCTION cme_obter_conj_disp_estoque ( nr_seq_conjunto_p bigint, qt_conjunto_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_conjunto_pend_w	bigint;
qt_conjunto_ester_w	bigint;
qt_total_pend_w		bigint;


BEGIN

select	sum(a.qt_conjunto)
into STRICT	qt_conjunto_pend_w
from	cm_requisicao_item a,
	cm_requisicao b
where	a.nr_seq_requisicao = b.nr_sequencia
and	b.cd_estabelecimento = cd_estabelecimento_p
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and	coalesce(a.cd_motivo_baixa::text, '') = ''
and	a.nr_seq_conjunto = nr_seq_conjunto_p;

select	count(*)
into STRICT	qt_conjunto_ester_w
from	cm_conjunto_cont a
where	a.nr_seq_conjunto  = nr_seq_conjunto_p
and 	not exists ( SELECT 1 from cm_requisicao_conj x where x.nr_seq_conj_real = a.nr_sequencia)
and	a.ie_status_conjunto = 3
and	a.cd_estabelecimento = cd_estabelecimento_p
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(a.dt_validade,trunc(clock_timestamp())) >= trunc(clock_timestamp());

qt_total_pend_w	:= (coalesce(qt_conjunto_pend_w,0) + coalesce(qt_conjunto_p,0));

if (qt_total_pend_w > qt_conjunto_ester_w) then
	ds_retorno_w	:= 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
