CREATE OR REPLACE FUNCTION cme_obter_dados_equip (nr_seq_equipamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p = 'M' - Método esterilização
*/
ds_classificacao_w	varchar(50);


BEGIN

select	b.ds_classificacao
into STRICT	ds_classificacao_w
from	cm_classif_metodo b,
	cm_equipamento a
where	a.nr_sequencia = nr_seq_equipamento_p
and	b.nr_sequencia = a.nr_seq_classif;

if (ie_opcao_p = 'M') then
	return ds_classificacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
