CREATE OR REPLACE FUNCTION cme_obter_local_cliente ( nr_seq_cliente_p bigint, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_local_w				varchar(60);


BEGIN

if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin
	select	coalesce(max(ds_local),'')
	into STRICT	ds_local_w
	from	cm_cliente
	where	nr_sequencia = nr_seq_cliente_p;
	end;
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	select	coalesce(max(ds_local),'')
	into STRICT	ds_local_w
	from	cm_cliente
	where	cd_cgc = cd_cgc_p;
end if;

return	ds_local_w;
end;
$body$
