SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cme_obter_nome_conjunto (nr_seq_conjunto_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(200);
ds_erro_w		varchar(2000) := '';
cd_codigo_w		varchar(10) := '';


BEGIN
SELECT * FROM cm_converte_cod_barras(nr_seq_conjunto_p, cd_codigo_w, ds_erro_w) INTO STRICT cd_codigo_w, ds_erro_w;

if (cd_codigo_w = '')then
	select NR_SEQ_CONJUNTO
	into STRICT ds_retorno_w
	from CM_CONJUNTO_CONT
	where NR_SEQUENCIA = cd_codigo_w;
end if;

select	nm_conjunto
into STRICT	ds_retorno_w
from	cm_conjunto
where	nr_sequencia = cd_codigo_w;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
