CREATE OR REPLACE FUNCTION cme_obter_se_item_conjunto (nr_seq_item_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	bigint := 0;


BEGIN

	select	sum(qt_registros) qt_registros
	into STRICT	nr_retorno_w
	from (	SELECT	count(*) qt_registros
				from	cm_conjunto_cont
				where	nr_seq_item = nr_seq_item_p
				and	ie_status_conjunto not in (6,10)
				
union

				SELECT	count(*) qt_registros
				from	cm_item_cont a,
					cm_conjunto_cont c
				where	 c.nr_sequencia = a.nr_seq_conjunto
				and	 a.nr_seq_item = nr_seq_item_p
				and	 exists (select 1 from cm_conjunto_cont b
				where        b.nr_sequencia = a.nr_seq_conjunto
				and            b.ie_status_conjunto not in (6,10))
	) alias6;


return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
