SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cme_obter_se_limite_esteril (nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


qt_conj_esterilizados_w		bigint;
qt_limite_esterilizacao_w	bigint;
ie_limite_w			varchar(1);


BEGIN

ie_limite_w	:= 'N';

if (nr_seq_conjunto_p IS NOT NULL AND nr_seq_conjunto_p::text <> '') then
	begin
	select	coalesce(max(qt_limite_esterilizacao),0)
	into STRICT	qt_limite_esterilizacao_w
	from	cm_conjunto
	where	nr_sequencia	= nr_seq_conjunto_p
	and	ie_situacao	= 'A';

	if (qt_limite_esterilizacao_w <> 0) then
		begin
		select	count(*)
		into STRICT	qt_conj_esterilizados_w
		from	cm_conjunto_cont
		where	nr_seq_conjunto = nr_seq_conjunto_p
		and	ie_status_conjunto = 3
		and	coalesce(ie_situacao,'A') = 'A';

		if (qt_conj_esterilizados_w >= qt_limite_esterilizacao_w) then
			ie_limite_w	:= 'S';
		end if;
		end;
	end if;
	end;
end if;

return ie_limite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
